import React from 'react';
import './App.css';
import {primary, secondary, tertiary, success, warning, danger, neutralCool, neutral} from './base_colors/meda2'
import Swatcher from './swatcher'

const swatchWeights = ['900', '800', '700', '600', '500', '400', '300', '200', '100', '075', '050', '025'];
const swatchLabel = "lightness"

let swatcher = new Swatcher(96, 0.3)

function renderSwatchRow(tints, key) {

  let swatch900 = document.getElementById(key + "-" + swatcher.swatchWeights[0])
  let swatch800 = document.getElementById(key + "-" + swatchWeights[1])
  let swatch700 = document.getElementById(key + "-" + swatchWeights[2])
  let swatch600 = document.getElementById(key + "-" + swatchWeights[3])
  let swatch500 = document.getElementById(key + "-" + swatchWeights[4])
  let swatch400 = document.getElementById(key + "-" + swatchWeights[5])
