  let swatch200 = document.getElementById(key + "-" + swatchWeights[7])
  let swatch100 = document.getElementById(key + "-" + swatchWeights[8])
  let swatch075 = document.getElementById(key + "-" + swatchWeights[9])
  let swatch050 = document.getElementById(key + "-" + swatchWeights[10])
  let swatch025 = document.getElementById(key + "-" + swatchWeights[11])

  swatch900.style.background = tints.swatch900
  swatch800.style.background = tints.swatch800
  swatch700.style.background = tints.swatch700
  swatch600.style.background = tints.swatch600
  swatch500.style.background = tints.swatch500
  swatch400.style.background = tints.swatch400
  swatch300.style.background = tints.swatch300
  swatch200.style.background = tints.swatch200
  swatch100.style.background = tints.swatch100
  swatch075.style.background = tints.swatch075
  swatch050.style.background = tints.swatch050
  swatch025.style.background = tints.swatch025

  swatch900.style.color = tints.swatch900
  swatch800.style.color = tints.swatch800
  swatch700.style.color = tints.swatch700
  swatch600.style.color = tints.swatch600
  swatch500.style.color = tints.swatch500
  swatch400.style.color = tints.swatch400
  swatch300.style.color = tints.swatch300
  swatch200.style.color = tints.swatch200
  swatch100.style.color = tints.swatch100
  swatch075.style.color = tints.swatch075
  swatch050.style.color = tints.swatch050
  swatch025.style.color = tints.swatch025

  swatch900.textContent = tints.swatch900
  swatch800.textContent = tints.swatch800
  swatch700.textContent = tints.swatch700
  swatch600.textContent = tints.swatch600
  swatch500.textContent = tints.swatch500
  swatch400.textContent = tints.swatch400
  swatch300.textContent = tints.swatch300
  swatch200.textContent = tints.swatch200
  swatch100.textContent = tints.swatch100
  swatch075.textContent = tints.swatch075
  swatch050.textContent = tints.swatch050
  swatch025.textContent = tints.swatch025

  if (swatchLabel == "hex" || swatchLabel === "lightness") {

    swatch900.style.color = swatcher.isDark(tints.swatch900) ? "#FFFFFF" : "#000000"
    swatch800.style.color = swatcher.isDark(tints.swatch800) ? "#FFFFFF" : "#000000"
    swatch700.style.color = swatcher.isDark(tints.swatch700) ? "#FFFFFF" : "#000000"
    swatch600.style.color = swatcher.isDark(tints.swatch600) ? "#FFFFFF" : "#000000"
    swatch500.style.color = swatcher.isDark(tints.swatch500) ? "#FFFFFF" : "#000000"
    swatch400.style.color = swatcher.isDark(tints.swatch400) ? "#FFFFFF" : "#000000"
    swatch300.style.color = swatcher.isDark(tints.swatch300) ? "#FFFFFF" : "#000000"
    swatch200.style.color = swatcher.isDark(tints.swatch200) ? "#FFFFFF" : "#000000"
    swatch100.style.color = swatcher.isDark(tints.swatch100) ? "#FFFFFF" : "#000000"
    swatch075.style.color = swatcher.isDark(tints.swatch075) ? "#FFFFFF" : "#000000"
    swatch050.style.color = swatcher.isDark(tints.swatch050) ? "#FFFFFF" : "#000000"
    swatch025.style.color = swatcher.isDark(tints.swatch025) ? "#FFFFFF" : "#000000"

    if (swatchLabel === "hex") {

      swatch900.textContent = tints.swatch900
      swatch800.textContent = tints.swatch800
      swatch700.textContent = tints.swatch700
      swatch600.textContent = tints.swatch600
      swatch500.textContent = tints.swatch500
      swatch400.textContent = tints.swatch400
      swatch300.textContent = tints.swatch300
      swatch200.textContent = tints.swatch200
      swatch100.textContent = tints.swatch100
      swatch075.textContent = tints.swatch075
      swatch050.textContent = tints.swatch050
      swatch025.textContent = tints.swatch025

    } else if (swatchLabel === "lightness") {

      swatch900.textContent = swatcher.getLightnessValue(tints.swatch900)
      swatch800.textContent = swatcher.getLightnessValue(tints.swatch800)
      swatch700.textContent = swatcher.getLightnessValue(tints.swatch700)
      swatch600.textContent = swatcher.getLightnessValue(tints.swatch600)
      swatch500.textContent = swatcher.getLightnessValue(tints.swatch500)
      swatch400.textContent = swatcher.getLightnessValue(tints.swatch400)
      swatch300.textContent = swatcher.getLightnessValue(tints.swatch300)
      swatch200.textContent = swatcher.getLightnessValue(tints.swatch200)
      swatch100.textContent = swatcher.getLightnessValue(tints.swatch100)
      swatch075.textContent = swatcher.getLightnessValue(tints.swatch075)
      swatch050.textContent = swatcher.getLightnessValue(tints.swatch050)
      swatch025.textContent = swatcher.getLightnessValue(tints.swatch025)

    }

  }

}

function renderJSON(primary, secondary, tertiary, success, warning, danger, neutralCool, neutral) {

  let result = []

  result.push(swatchOutput(primary, "primary"))
  result.push(swatchOutput(secondary, "secondary"))
  result.push(swatchOutput(tertiary, "tertiary"))
  result.push(swatchOutput(success, "success"))
  result.push(swatchOutput(warning, "warning"))
  result.push(swatchOutput(danger, "danger"))
  result.push(swatchOutput(neutralCool, "neutral-cool"))
  result.push(swatchOutput(neutral, "neutral"))

  return JSON.stringify(result)

}


function swatchOutput(swatches, name) {
  return {
    name: name,
    swatches: {
      900: swatches.swatch900,
      800: swatches.swatch800,
      700: swatches.swatch700,
      600: swatches.swatch600,
      500: swatches.swatch500,
      400: swatches.swatch400,
      300: swatches.swatch300,
      200: swatches.swatch200,
      100: swatches.swatch100,
      75: swatches.swatch075,
      50: swatches.swatch050,
      25: swatches.swatch025
    }
  }
}

const drawWeightScale = () => {
  const result = []
  for (const [index, value] of swatchWeights.entries()) {
    let id = "weight" + value
    result.push(<div className="swatch" id={id}><h4>{value}</h4></div>)
  }
  return result;
}

const drawSwatchRow = (key) => {
  const result = []
  for (const [index, value] of swatchWeights.entries()) {
    let id = key + "-" + value
    result.push( <div className="swatch" id={id}/> ) 
  }
  return result;
}


class App extends React.Component {

  componentDidMount() {

    let primarySwatchRow = swatcher.createSwatchRow(primary)
    let secondarySwatchRow = swatcher.createSwatchRow(secondary)
    let tertiarySwatchRow = swatcher.createSwatchRow(tertiary)
    let successSwatchRow = swatcher.createSwatchRow(success)
    let warningSwatchRow = swatcher.createSwatchRow(warning)
    let dangerSwatchRow = swatcher.createSwatchRow(danger)
    let neutralCoolSwatchRow = swatcher.createSwatchRow(neutralCool)
    let neutralSwatchRow = swatcher.createSwatchRow(neutral)

    renderSwatchRow(primarySwatchRow, "primary")
    renderSwatchRow(secondarySwatchRow, "secondary")
    renderSwatchRow(tertiarySwatchRow, "tertiary")
    renderSwatchRow(successSwatchRow, "success")
    renderSwatchRow(warningSwatchRow, "warning")
    renderSwatchRow(dangerSwatchRow, "danger")
    renderSwatchRow(neutralCoolSwatchRow, "neutral-cool")
    renderSwatchRow(neutralSwatchRow, "neutral")

    let output = renderJSON(primarySwatchRow, secondarySwatchRow,
      tertiarySwatchRow, successSwatchRow, warningSwatchRow,
      dangerSwatchRow, neutralCoolSwatchRow, neutralSwatchRow)

    console.log(output)
  }

  render() {

    return (

      <div className="App">
        <div>{drawWeightScale()}</div>
        <div>{drawSwatchRow("primary")}</div>
        <div>{drawSwatchRow("secondary")}</div>
        <div>{drawSwatchRow("tertiary")}</div>
        <div>{drawSwatchRow("success")}</div>
        <div>{drawSwatchRow("warning")}</div>
        <div>{drawSwatchRow("danger")}</div>
        <div>{drawSwatchRow("neutral-cool")}</div>
        <div>{drawSwatchRow("neutral")}</div>
      </div>
    );
  }

}

export default App;
