import React from 'react';
import './App.css';
import tinycolor from 'tinycolor2'
import convert from 'color-convert'
import {primary, secondary, tertiary, success, warning, danger, neutralCool, neutral} from './base_colors/meda2'
import swatcher from './swatcher'

const swatchWeights = ['900', '800', '700', '600', '500', '400', '300', '200', '100', '075', '050', '025'];
const swatchLabel = "lightness"
const paperWhite = 96
const shadeTargetMultiplier = 0.3

let swatchMaker = swatcher(paperWhite, shadeTargetMultiplier)

function renderSwatchRow(tints, key) {

  let swatch900 = document.getElementById(key + "-" + swatchWeights[0])
  let swatch800 = document.getElementById(key + "-" + swatchWeights[1])
