  let swatch300 = document.getElementById(key + "-" + swatchWeights[6])
  let swatch200 = document.getElementById(key + "-" + swatchWeights[7])
  let swatch100 = document.getElementById(key + "-" + swatchWeights[8])
  let swatch075 = document.getElementById(key + "-" + swatchWeights[9])
  let swatch050 = document.getElementById(key + "-" + swatchWeights[10])
  let swatch025 = document.getElementById(key + "-" + swatchWeights[11])

  swatch900.style.background = tints.swatch900
  swatch800.style.background = tints.swatch800
  swatch700.style.background = tints.swatch700
  swatch600.style.background = tints.swatch600
  swatch500.style.background = tints.swatch500
  swatch400.style.background = tints.swatch400
  swatch300.style.background = tints.swatch300
  swatch200.style.background = tints.swatch200
  swatch100.style.background = tints.swatch100
  swatch075.style.background = tints.swatch075
  swatch050.style.background = tints.swatch050
  swatch025.style.background = tints.swatch025

  swatch900.style.color = tints.swatch900
  swatch800.style.color = tints.swatch800
  swatch700.style.color = tints.swatch700
  swatch600.style.color = tints.swatch600
  swatch500.style.color = tints.swatch500
  swatch400.style.color = tints.swatch400
  swatch300.style.color = tints.swatch300
  swatch200.style.color = tints.swatch200
  swatch100.style.color = tints.swatch100
  swatch075.style.color = tints.swatch075
  swatch050.style.color = tints.swatch050
  swatch025.style.color = tints.swatch025

  swatch900.textContent = tints.swatch900
  swatch800.textContent = tints.swatch800
  swatch700.textContent = tints.swatch700
  swatch600.textContent = tints.swatch600
  swatch500.textContent = tints.swatch500
  swatch400.textContent = tints.swatch400
  swatch300.textContent = tints.swatch300
  swatch200.textContent = tints.swatch200
  swatch100.textContent = tints.swatch100
  swatch075.textContent = tints.swatch075
  swatch050.textContent = tints.swatch050
  swatch025.textContent = tints.swatch025

  if (swatchLabel == "hex" || swatchLabel === "lightness") {

    swatch900.style.color = tinycolor(tints.swatch900).isDark() ? "#FFFFFF" : "#000000"
    swatch800.style.color = tinycolor(tints.swatch800).isDark() ? "#FFFFFF" : "#000000"
    swatch700.style.color = tinycolor(tints.swatch700).isDark() ? "#FFFFFF" : "#000000"
    swatch600.style.color = tinycolor(tints.swatch600).isDark() ? "#FFFFFF" : "#000000"
    swatch500.style.color = tinycolor(tints.swatch500).isDark() ? "#FFFFFF" : "#000000"
    swatch400.style.color = tinycolor(tints.swatch400).isDark() ? "#FFFFFF" : "#000000"
    swatch300.style.color = tinycolor(tints.swatch300).isDark() ? "#FFFFFF" : "#000000"
    swatch200.style.color = tinycolor(tints.swatch200).isDark() ? "#FFFFFF" : "#000000"
    swatch100.style.color = tinycolor(tints.swatch100).isDark() ? "#FFFFFF" : "#000000"
    swatch075.style.color = tinycolor(tints.swatch075).isDark() ? "#FFFFFF" : "#000000"
    swatch050.style.color = tinycolor(tints.swatch050).isDark() ? "#FFFFFF" : "#000000"
    swatch025.style.color = tinycolor(tints.swatch025).isDark() ? "#FFFFFF" : "#000000"

    if (swatchLabel === "hex") {

      swatch900.textContent = tints.swatch900
      swatch800.textContent = tints.swatch800
      swatch700.textContent = tints.swatch700
      swatch600.textContent = tints.swatch600
      swatch500.textContent = tints.swatch500
      swatch400.textContent = tints.swatch400
      swatch300.textContent = tints.swatch300
      swatch200.textContent = tints.swatch200
      swatch100.textContent = tints.swatch100
      swatch075.textContent = tints.swatch075
      swatch050.textContent = tints.swatch050
      swatch025.textContent = tints.swatch025

    } else if (swatchLabel === "lightness") {

      swatch900.textContent = getLightnessValue(tints.swatch900)
      swatch800.textContent = getLightnessValue(tints.swatch800)
      swatch700.textContent = getLightnessValue(tints.swatch700)
      swatch600.textContent = getLightnessValue(tints.swatch600)
      swatch500.textContent = getLightnessValue(tints.swatch500)
      swatch400.textContent = getLightnessValue(tints.swatch400)
      swatch300.textContent = getLightnessValue(tints.swatch300)
      swatch200.textContent = getLightnessValue(tints.swatch200)
      swatch100.textContent = getLightnessValue(tints.swatch100)
      swatch075.textContent = getLightnessValue(tints.swatch075)
      swatch050.textContent = getLightnessValue(tints.swatch050)
      swatch025.textContent = getLightnessValue(tints.swatch025)

    }

  }

}

function createShadeTargets(base) {
  let L_400 = getLightnessValue(base)
  let L_900 = L_400 * shadeTargetMultiplier

  let stepValue = (L_400 - L_900) / 5

  let L_800 = L_900 + (stepValue * 1)
  let L_700 = L_900 + (stepValue * 2)
  let L_600 = L_900 + (stepValue * 3)
  let L_500 = L_900 + (stepValue * 4)

  return {
    L_900: L_900,
    L_800: L_800,
    L_700: L_700,
    L_600: L_600,
    L_500: L_500
  }
}

function createTintTargets(base) {
  let L_400 = getLightnessValue(base)

  let stepValue = (paperWhite - L_400) / 6

  let L_300 = L_400 + (stepValue * 1)
  let L_200 = L_400 + (stepValue * 2)
  let L_100 = L_400 + (stepValue * 3)
  let L_075 = L_400 + (stepValue * 4)
  let L_050 = L_400 + (stepValue * 5)

  return {
    L_300: L_300,
    L_200: L_200,
    L_100: L_100,
    L_075: L_075,
    L_050: L_050,
    L_025: paperWhite,
  }
}

function darkenToTarget(color, targetValue) {
  let result = color.clone()

  while (getLightnessValue(result) > targetValue) {
    result = darken(result)
  }

  return result

}

function lightenToTarget(color, targetValue) {
  let result = color.clone()

  while (getLightnessValue(result) < targetValue) {
    result = lighten(result)
  }

  return result

}

function getLightnessValue(hexString) {
  return convert.hex.lab(hexString)[0]
}

function lighten(color) {
  return tinycolor(color.clone()).lighten(1)
}

function darken(color) {
  return tinycolor(color.clone()).darken(1)
}

function createSwatchRow(hexString) {

  let color = tinycolor(hexString)
  let shadeTargets = createShadeTargets(color)
  let tintTargets = createTintTargets(color)

  let swatch900 = darkenToTarget(color.clone().saturate(10), shadeTargets.L_900)
  let swatch800 = darkenToTarget(color.clone().saturate(5), shadeTargets.L_800)
  let swatch700 = darkenToTarget(color.clone().saturate(3), shadeTargets.L_700)
  let swatch600 = darkenToTarget(color.clone().saturate(2), shadeTargets.L_600)
  let swatch500 = darkenToTarget(color.clone().saturate(0), shadeTargets.L_500)
  let swatch400 = color
  let swatch300 = lightenToTarget(color.clone().saturate(0), tintTargets.L_300)
  let swatch200 = lightenToTarget(color.clone().saturate(3), tintTargets.L_200)
  let swatch100 = lightenToTarget(color.clone().saturate(5), tintTargets.L_100)
  let swatch075 = lightenToTarget(color.clone().saturate(6), tintTargets.L_075)
  let swatch050 = lightenToTarget(color.clone().saturate(8), tintTargets.L_050)
  let swatch025 = lightenToTarget(color.clone().saturate(25), tintTargets.L_025)

  return {
    swatch900: swatch900.toHexString(),
    swatch800: swatch800.toHexString(),
    swatch700: swatch700.toHexString(),
    swatch600: swatch600.toHexString(),
